const float sp= 0.3;		//speed of movement

#define pi 3.141592653589793238462643383279
const float B = 4./pi;
const float C = -4./(pi*pi);

vec2 sine(vec2 x)
{
    x = fract( (x+pi)/(2.*pi) )*(2.*pi)-pi;
    return B*x + C*x*abs(x);
}

float sine(float x)
{
    x = fract( (x+pi)/(2.*pi) )*(2.*pi)-pi;
    return B*x + C*x*abs(x);
}

vec2 cosine( vec2 x )
{
    return sine( x+(pi/2.) );
}

vec3 rgbplasma(vec2 uv, float time)
{
	uv+=0.3*sine(4.0*uv.yx+time);
	uv+=0.2*sine(10.0*uv.yx+time);
	uv+=0.15*sine(20.0*uv.yx+time);
	uv+=0.1*sine(30.0*uv.yx+time);

	return vec3(cosine(uv), sine(uv.y+uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iMouse.xy) / iResolution.xx;

    fragColor = vec4(rgbplasma(4.*uv,iTime*sp), 1.0);
}
