#define cellsize 50.0
//line distance by iq
float line( vec2 p, vec2 a, vec2 b ) 
{
    vec2 pa = p-a, ba = b-a;
    float h = clamp( dot(pa,ba)/dot(ba,ba), 0.0, 1.0 );
    return length( pa - ba*h );
}

bool getcellbool(vec2 pos) {
    pos -=0.5;
    return texelFetch(iChannel0, ivec2(pos*cellsize*0.49+iResolution.xy*0.5),0).x>0.0;
}

float getcell(vec2 pos) {
    pos -=0.5;
    return texelFetch(iChannel0, ivec2(pos*cellsize*0.49+iResolution.xy*0.5),0).x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ((fragCoord*2.0-iResolution.xy)/cellsize)+0.5;
    vec2 muv = ((iMouse.xy*2.0-iResolution.xy)/cellsize);

    vec2 orig = floor(uv)+0.5;

    //vec2 closestcell = vec2(0.5);
    //bool isfilled = getcell(orig);

    float len = length(orig-uv);

    vec2 c01 = vec2(-0.5, 0.5);
    vec2 c11 = vec2( 0.5, 0.5);
    vec2 c10 = vec2( 0.5,-0.5);
    vec2 c00 = vec2(-0.5,-0.5);
    float mass01 = getcell(orig+c01);
    float mass11 = getcell(orig+c11);
    float mass10 = getcell(orig+c10);
    float mass00 = getcell(orig+c00);

	vec2 inv = vec2(-1.0,1.0);
    fragColor = max(vec4(1.0-4.0*length(orig-uv+c00)),vec4(0.0));
    fragColor = max(vec4(1.0-4.0*length(orig-uv+c01)),fragColor);
    fragColor = max(vec4(1.0-4.0*length(orig-uv+c11)),fragColor);
    fragColor = max(vec4(1.0-4.0*length(orig-uv+c10)),fragColor);

    for (int i = 0; i < 4; i++) {
        if (getcellbool(orig+c00)) {
            vec2 pos1 = vec2(10.0);
            vec2 pos2 = vec2(10.0);
            if (getcellbool(orig+c01)&&getcellbool(orig+c10)||getcellbool(orig+c11)){

                if((mass11>0.0)!=((mass01>0.0)||(mass10>0.0))) {
                    pos1 = mix(c10,c11,abs(sign(mass11)-max(mass10,mass11)));
                    pos2 = mix(c01,c11,abs(sign(mass11)-max(mass01,mass11)));
                }
            } else if (getcellbool(orig+c01)) {
                pos1 = mix(c00,c10,max(mass00,mass10));
                pos2 = mix(c01,c11,max(mass01,mass11));
            } else if (getcellbool(orig+c10)) {
                pos1 = mix(c00,c01,max(mass00,mass01));
                pos2 = mix(c10,c11,max(mass10,mass11));
            } else {
                pos1 = mix(c00,c01,max(mass00,mass01));
                pos2 = mix(c00,c10,max(mass00,mass10));
            }
            fragColor = max(vec4(1.0-10.0*line(uv, orig+pos1, orig+pos2)),fragColor);

        }
        vec2 back1 = c00;
        c00 = c01;
        c01 = c11;
        c11 = c10;
        c10 = back1;

        float back2 = mass00;
        mass00 = mass01;
        mass01 = mass11;
        mass11 = mass10;
        mass10 = back2;
	}

    //fragColor = mix(fragColor, texture(iChannel0,fragCoord.xy/iResolution.xy),0.5);
}

#define cellsize 50.0

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    //setting the previous frame pixel color to f
    fragColor = texelFetch(iChannel0, ivec2(fragCoord),0);

    //if the length from mouse is lower than length from previous mouse positions, set this length to closest and set a random color



    vec2 uv = floor((fragCoord*2.0-iResolution.xy)/cellsize);
    vec2 muv = floor((iMouse.xy*2.0-iResolution.xy)/cellsize);
    if (iMouse.z > 0.0 && uv == muv)
    fragColor = clamp(iTimeDelta+fragColor,vec4(0.0),vec4(1.0));
}
