//Booleans for turning layers on/off
bool houseOn = true;
bool chimneyOn = true;
bool windowOn = true;

//Variables for house
vec2 housePos = vec2(0.5,0.2);
float houseX = 0.3;
float houseY = 0.15;

//variables for chimney
vec2 chimneyPos = vec2(0.7,0.35);
float chimneyX = 0.05;
float chimneyY = 0.1;

//variables for windows
vec2 windowPos = vec2(0.4, 0.15);
vec2 window2Pos = vec2 (0.6, 0.15);
float windowX = 0.03;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	uv.y *= iResolution.y / iResolution.x;

    //LAYERS
    vec4 BG = vec4(uv,0.5+0.5*sin(iTime),1.0);
    vec4 house;
    vec4 chimney;
    vec4 window;
    vec4 window2;

    float houseXstep = step(length(housePos.x - uv.x), houseX);
    float houseYstep = step(length(housePos.y - uv.y), houseY);

    float chimXstep = step(length(chimneyPos.x - uv.x), chimneyX);
    float chimYstep = step(length(chimneyPos.y - uv.y), chimneyY);

    float winXstep = step(length(windowPos.x - uv.x), windowX);
    float winYstep = step(length(windowPos.y - uv.y), windowX);

    float win2Xstep = step(length(window2Pos.x - uv.x), windowX);
    float win2Ystep = step(length(window2Pos.y - uv.y), windowX);

    if(houseXstep>0.0 && houseYstep>0.0 && houseOn){
     house = vec4 (1.0,0.0,0.0,1.0);  
    }

    if(chimXstep>0.0 && chimYstep>0.0 && chimneyOn){
     chimney = vec4 (0.0,1.0,0.0,1.0);  
    }

    if(winXstep>0.0 && winYstep>0.0 && windowOn){
     window = vec4 (0.0,0.0,1.0,1.0);   
    }

    if(win2Xstep>0.0 && win2Ystep>0.0 && windowOn){
     window = vec4 (0.0,0.0,1.0,1.0);   
    }

    vec4 Component = mix(BG, house, house.a);
    Component = mix(Component, chimney, chimney.a);
    Component = mix(Component, window, window.a);
    Component = mix(Component, window2, window2.a);

    fragColor = Component;
}
