float df(vec2 p, vec2 a, vec2 b, vec2 c)
{
	p *= 1.5;
    float t = length(p)*0.29;
    vec2 pt = mix(mix(a,b,t),mix(b,c,t),t);
	//pt = mix(a,b,length(c));
    return length(p-pt);
}

vec2 move(vec2 g, vec2 p, float t)
{
   return sin( t * 2. + 9. * fract(sin((floor(g)+p)*mat2(2,7,2,5))));
}

void mainImage( out vec4 f, vec2 g )
{
    vec2 uvt = g / iResolution.xy;

    vec2 p = g /= iResolution.y / 16., a0,a1,a2; f-=f-9.;

	float t = iTime;

    for(int x=-2;x<=2;x++)
    for(int y=-2;y<=2;y++)
        p = vec2(x,y),
        a0 = move(g,p, t),
        a1 = move(g,p, t+1.),
        a2 = move(g,p, t+1.),
        p += .5 + .5 * a0 - fract(g),
        f = min(f, df(p,a0,a1,a2));

    f = sqrt(vec4(.5,1,1,1)-mix(0., 1., f.x));

}
