void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord.xy / iResolution.xy; 
    vec4 texColor = texture(iChannel0,xy); 

    float timeBit = abs(sin(iTime / 4.0));

    float gray = dot(texColor.rgb, vec3(0.299, 0.587, 0.114));
    gray = (gray - 0.5) * 2.0;
    if (gray < 0.0) {
     	gray += 1.0;   
    }

    float redShift = gray + timeBit; 
    if (redShift > 1.0) {
     	redShift -= 1.0;   
    }

    texColor.r = 0.0;
    texColor.g = redShift;
    texColor.b = redShift;
        ;
    fragColor = texColor; 

}
