void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t = iTime * 0.5;
    float size = 30.0;
    vec2 uv = fragCoord.xy / iResolution.xy * size - size/2.0;
    float c =
        (size/10.0) / length(
            vec2(
                uv.x + tan(sin(t) * (sin(uv.y) + uv.x)),
                uv.y + tan(cos(t) * (cos(uv.x) + uv.y))));
	fragColor = vec4(c,0.0,c,1.0);
}
