void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - vec2(.5);

    uv *= 8.;

    float n = 0.;
    if (iMouse.w > 0.) n = iMouse.y/iResolution.y*2.-1.;

    mat2 m;
    for (int i = 1; i < 4; i++) {
        m = mat2(sin(length(uv)*float(i)),cos(atan(uv.y,uv.x)),
                 0., n);
        uv *= m;
    }

    float cl = ceil(length(uv)*100.)/10.;
	fragColor = (length(uv)-.2) * vec4(mod(cl+iTime,1.),mod(cl+6.2464+iTime,1.),mod(cl+3.4365+iTime,1.),1.);
}
