void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (fragCoord.xy-iResolution.xy/2.) / min(iResolution.x,iResolution.y) *2.;
    uv /= exp(sin(iTime/5.)*4.+4.);

    if (abs(uv.y) > abs(uv.x)) {
        uv = vec2(uv.y, uv.x);
    }
    uv.x = abs(uv.x);
    uv.x -= 1.;
    for (int i = 0; i < 100; i++) {
    uv = -uv+vec2(uv.x*uv.x-uv.y*uv.y,2.*uv.x*uv.y);
        if (length(uv) > 2.) {
            uv = vec2(10,0);
        }
    }
    float f = step(0.,(mod(uv.x*20.,1.)-0.5)*(mod(uv.y*20.,1.)-0.5));
    //float f = length(uv);
	fragColor = vec4(f,f,f,1.0);
}
