void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
//variables for # of rows and columns
float rows = 10.0;
float columns = 2.0;   

vec2 uv = fragCoord.xy / iResolution.xy;

//textures for Van Damme and 1961 commercial
vec4 texture0 = texture(iChannel0, uv);
vec4 texture1 = texture(iChannel1, uv);
fragColor = texture0;

    //mod gets around needing a loop
    //need to use xor, otherwise you end up with a grid
if (mod(rows/2.0*uv.x, 1.0) < 0.5 ^^ (mod(columns/2.0*uv.y, 1.0) < 0.5))
{
    //lets Van Damme appear through 1961 commercial
    fragColor = texture0*texture1;
}





}
