// Validation of IQ's https://iquilezles.org/articles/distance code example producing 
// shape https://iquilezles.org/articles/distance/gfx03.jpg

float f(vec2 p) // 
{
	float  r = length(p);float a = atan(p.y,p.x);
    return r-1.+0.5*sin(3.*a+2.*r*r);   
}

vec2 grad( in vec2 x )
{
    vec2 h = vec2( 0.01, 0.0 );
    return vec2( f(x+h.xy) - f(x-h.xy),
                 f(x+h.yx) - f(x-h.yx) )/(2.0*h.x);
}

float color( in vec2 x )
{
    float v = f( x );
    vec2  g = grad( x );
    float de = abs(v)/length(g);
    return smoothstep( 1.0, 2.0, de*(iResolution.y/2.0/1.5) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord.xy-iResolution.xy) / iResolution.y;
    uv *= 1.5; 
    fragColor = vec4(1.0-color(uv));
}
