renderpass.code:
vec3 pal(float t, vec3 a, vec3 b, vec3 c, vec3 d) {return a + b*cos(6.28318*(c*t+d));}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    vec2 uv = fragCoord.xy / iResolution.xy;

    vec3 color = vec3(false);
    float seed = sin(iTime*0.5+0.5);

    if(.1 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(0,.33,.67));}

    if(uv.x > .1 && .2>= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed));}

    if(uv.x > .2 && .3 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed*10.0,.33,.67));}

    if(uv.x > .3 && .4 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed*9.5,.33,.67));}

    if(uv.x > .4 && .5 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed*8.0, .33,.67));}

    if(uv.x > .5 && .6 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed*7.5,.33,.67));}

    if(uv.x > .6 && .7 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(seed*7.0,.33,.67));}

    if(uv.x > .7 && .8 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(.0,.33+seed,.67));}

    if(uv.x > .8 && .9 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(.0,.33,.67-seed));}

    if(uv.x > .9 && 1.0 >= uv.x) {color = pal(uv.y, vec3(.5), vec3(.5), vec3(1), vec3(.0*seed,.33*seed,.67*seed));}

    float f = fract(uv.x*10.0);

    // BORDER
    color *= vec3(smoothstep(0.49, 0.47, abs(f-0.5))); // fract はシマシマを描くのに役に立つ

    // SHADOW
    color *= vec3(0.5+0.5*sqrt(4.0*f*(1.0-f))); // sqrt はグラデーションを 0〜1 のグラデーションをシャープにするのに役立つ(たぶん)

    fragColor = vec4(color, 1);
}
