void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy/iResolution.xy;

    uv *= 100.;
    uv += vec2(cos(uv.x/2.)*5.,cos(uv.y/2.)*5.);

	float f = (cos(uv.x)*.2+cos(uv.y)*.2)*cos((uv.x+uv.y)*length(uv))*5.;
    if (f > .7) {
        f-=.7;
        if (cos((uv.x+uv.y+iTime*10.)*sin(uv.y)) > .5) {
        fragColor = vec4(.85,.26,1.39,1.)*f;
        } else {
        fragColor = vec4(.1,.2,.3,1.)*f;    
        }
    } else {
        fragColor = vec4(.0);
    }
}
