vec3 lambdaToRgb(float Wavelength){
    float Red, Green, Blue;
    if((Wavelength >= 380.) && (Wavelength<440.)){
        Red = -(Wavelength - 440.) / (440. - 380.);
        Green = 0.0;
        Blue = 1.0;
    }else if((Wavelength >= 440.) && (Wavelength<490.)){
        Red = 0.0;
        Green = (Wavelength - 440.) / (490. - 440.);
        Blue = 1.0;
    }else if((Wavelength >= 490.) && (Wavelength<510.)){
        Red = 0.0;
        Green = 1.0;
        Blue = -(Wavelength - 510.) / (510. - 490.);
    }else if((Wavelength >= 510.) && (Wavelength<580.)){
        Red = (Wavelength - 510.) / (580. - 510.);
        Green = 1.0;
        Blue = 0.0;
    }else if((Wavelength >= 580.) && (Wavelength<645.)){
        Red = 1.0;
        Green = -(Wavelength - 645.) / (645. - 580.);
        Blue = 0.0;
    }else if((Wavelength >= 645.) && (Wavelength<781.)){
        Red = 1.0;
        Green = 0.0;
        Blue = 0.0;
    }else{
        Red = 0.0;
        Green = 0.0;
        Blue = 0.0;
    }

    return vec3(Red,Green,Blue);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = (2.*fragCoord.xy-iResolution.xy) / iResolution.xy;

    float lambda = mix(570.,700.,abs(sin(iTime/2.)));
	float dlambda = 180.+(150.-180.)*(lambda-580.)/(680.-580.);
    float lambdaComp = lambda - dlambda;

    if(uv.x<-0.02){
        fragColor = vec4(lambdaToRgb(lambda),1.0);
    } else if(uv.x>0.02){
        fragColor = vec4(lambdaToRgb(lambdaComp),1.0);
    }

    //fragColor = vec4(lambdaToRgb(1000.*(uv.x+1.)/2.),0);
}
