void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 size = vec2(8, 4);
    vec2 uv =  floor(fragCoord / iResolution.xy * size);// thanks to FabriceNeyret2
    vec3 colorLight = vec3(0.8);
    vec3 colorDark = vec3(0.2);
    int pic = int(iTime) % 2 == 0 ? 419962121 : 1497914697;
    if((pic & (1 << int(uv.x + uv.y * size.x))) != 0)
    {
        fragColor = vec4(colorDark, 1.0);
    }
	else
    {
        fragColor = vec4(colorLight, 1.0);
    }
}
