// simplified and golfed from https://www.shadertoy.com/view/ltBBzK

/**/

#define C(v)  v.x < e && v.y > 1.3 || v.y < e && v.x < .4 ||

void mainImage(out vec4 O, vec2 U) {
	float  r = 1.75, /* h=1.4, c=.4 */
           e =  9./iResolution.y;
    vec2 V = abs( mod(U*e, r+r) - r );
    O = vec4(   C(V) C(r-V)
                abs(V.x + V.y*r -2.4) < e
             || abs(V.y - V.x*r +.64) < e
            ); }

