void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy - 0.5;
    uv.x *= iResolution.x/iResolution.y;
    uv *= 9.0;

//    float shape = smoothstep( 1.0, 0.9, (length(uv)/0.5) );
//    vec3 sfc = vec3( sin(radians((uv.x/0.5)*90.0)), cos(radians((length(uv)/0.5)*90.0)), sin(radians((uv.y/0.5)*90.0)) );

//    vec3 sun = vec3( sin(iTime*0.3)*1.0, cos(iTime*0.3), 0.0 );

//    float moon = dot( (sun), (sfc) );
//    moon = clamp( moon*10.0, 0.0, 1.0 );

    vec3 tex = 0.6 + texture( iChannel0, uv*0.08 ).xyz*0.1 + texture( iChannel0, uv*0.02 ).zxy*0.3;

	//fragColor = vec4( normalize(sfc*0.5+vec3(0.5,0.5,0.5))*shape, 1.0);
//	fragColor = vec4( tex*moon*shape, 1.0);


    float phase = 0.2+sin(iTime*0.1)*0.8;

    vec3 sun = normalize(vec3( abs(1.0-phase)*sin(iTime*0.3), phase*2.0-1.0, abs(1.0-phase)*cos(iTime*0.3) ));



    float shape = smoothstep( 1.0, 0.9, (length(uv)/0.5) );

    float glare = (smoothstep( 10.0, 0.0, (length(uv)/0.5) ));

    float mh = cos(radians((length(uv)/0.5)*90.0));
    vec3 sfc = normalize(vec3(uv.x,mh,uv.y));

    float sm = dot( normalize(sfc), normalize(sun) );

    glare = glare * (dot( normalize(sfc), (vec3(sun.x,0.0,sun.z)) ) + sun.y);

    sm = pow( sm, 0.5 );


    float dither = texture( iChannel0, uv ).x * 0.02;

    float stars = smoothstep( 0.8, 1.0, texture( iChannel0, uv/8.0 ).y ) * smoothstep( 0.8, 1.0, texture( iChannel0, uv/2.0 ).x );

	fragColor = vec4( clamp(tex*shape*sm, 0.0, 1.0) + 0.05*glare*(1.0-shape) - dither + shape*0.015 + stars*1.0*(1.0-shape), 1.0 );

}
