float Circle(vec2 uv, vec2 p, float r, float blur) {
	float d = length(uv - p);
    float c = smoothstep(r, r-blur, d);   
    return c;
}
float Band(float t,float start, float end, float blur){
    float step1 = smoothstep(start - blur, start + blur, t);
    float step2 = smoothstep(end + blur, end - blur, t);
    return step1 * step2;
}

float Rect(vec2 uv, float left, float right, float bottom, float top, float blur, float size){
    uv /= size;
    float band1 = Band(uv.x, left, right, blur);
    float band2 = Band(uv.y, bottom, top, blur);
    return band1 * band2 ;
}

float Rect2(vec2 uv, float width, float height, float blur, float size){
    uv /= size;
    float band1 = Band(uv.x, -width, width, blur);
    float band2 = Band(uv.y, -height, height, blur);
    return band1 * band2 ;
}
float Smiley(vec2 uv, vec2 p, float size){
    uv -= p;
    uv /= size;

    float c = Circle(uv, vec2(0., 0.), .4, .03);
    c -= Circle(uv, vec2(-0.15, 0.1), .091, .01);
    c -= Circle(uv, vec2(0.15, 0.1), .091, .01);

    c += Circle(uv, vec2(-0.16, 0.046), .0211, .01);
    c += Circle(uv, vec2(0.14, 0.046), .0211, .01);

    float mouth = Circle(uv, vec2(0., 0.), .3, .02);
    mouth -= Circle(uv, vec2(0., 0.1), .3, .02);

    return c -= mouth;
}
float remap01(float a, float b, float t){
    return (t-a) / (b-a);
}
float remap(float a, float b, float c, float d, float t){
	return remap01(a, b, t) * (d-c) + c;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;// 0 <> 1
    float t = iTime;
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    vec3 col = vec3(0.);

    float c = .0;
    // float c = Smiley(uv, vec2(.0, .0), .5);

    float x = uv.x;
    float m = (x-.5)*(x+.5);
    m = m * m * 2.;

    m = sin(-t+x* 8.)*.1;
    float y = uv.y - m;

    float blur = remap(-.8,.8,.01,.25,x);
    blur = pow(blur * 4., 3.);
    c = Rect2(vec2(x,y), 1.0, .005, blur, 1.0);
    //c -= Smiley(uv, vec2(.0, .0), .5);

    //c = smoothstep(-.2, .2, uv.x);
    col = vec3(.4, .4, .4) * c;

	fragColor = vec4(col,1.0);
}
