#define PI 3.1415926535
#define TWOPI 2.0 * PI

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	//	get default sample position   
	vec2 uv = fragCoord.xy / iResolution.xy;

    //	wiggle your sample positions around a bit
    vec2 sp;
    sp.x = uv.x + 0.1 * sin( iTime * 1.0 + uv.y * TWOPI * 2.0);
    sp.y = uv.y + 0.1 * cos( iTime * 1.0 + uv.x * TWOPI * 2.0);

    //	make sure to wrap your edges
    sp.x = mod( sp.x, 1.0 );
    sp.y = mod( sp.y, 1.0 );

    //	sample the texture and set pixel color
    vec4 texture1 = texture( iChannel0, sp );    
    vec4 pc = texture1;    

	fragColor = vec4( pc );
}
