const float PI = 3.141592653589793;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

    vec2 offset = vec2(0, -0.2);

   	float r = sqrt(pow(0.5 - uv.x, 2.0) + pow(uv.y + offset.y, 2.0));
    float phi = atan(uv.y + offset.y, 0.5 - uv.x);

    float rn = r * 2.0; //abs(uv.x - 0.5); //2.0 * pow(r, 10.0);
    float nphi = PI;

    uv.x = -(rn * cos(nphi));
    uv.y = max(min(phi / (2.0 * PI), 0.5), 0.0);

    vec4 fft = texture(iChannel0, vec2(ceil(uv.x * 50.0)/50.0, 0));

    vec4 wave = texture(iChannel0, vec2(uv.x, 1));

	vec4 col = vec4(fft.x * 2.0, uv.x, 0.5+0.5*sin(iTime),1.0);
    col *= smoothstep(.25, 1.0, (sin(uv.x * 50.0 * PI) + 1.0)/2.0);

    if(uv.x + offset.x < 0.0 || uv.x + offset.x > 1.0 || 
       uv.y + offset.y > 1.0 || uv.y + offset.y < 0.0) 
    {
        col *= 0.0;
    }
    else {
    	col *= (1.0 - ceil(uv.y - fft));// * fft;
    }

    fragColor = col;
}
