void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.y;

    float len = length(vec2(uv.x-0.5, uv.y-0.5));
   if (len < 0.5) {
      vec3 linearColor = vec3(0.0, 0.0, 0.0);

      vec3 pos = vec3(uv.x-0.5, uv.y-0.5, 0.0);
      pos.z = -sqrt(0.25 - pow(pos.x, 2.0) - pow(pos.y, 2.0));
      vec3 normPos = normalize(pos);

      vec3 cameraPosition = vec3(0.0, 0.0, -55.0);

      vec3 lightPos = vec3((iMouse.x-iResolution.x*.5)*0.1, (iMouse.y-iResolution.y*.5)*0.1, -2.0);

      vec3 surfaceToLight = normalize(lightPos - pos);
      vec3 surfaceToCamera = normalize(cameraPosition - pos);

      vec3 surfaceColor = vec3(0.0, 0.0, 1.0);
      vec3 ambientColor = vec3(0.0, 1.0, 0.0);
      vec3 specularColor = vec3(1.0, 1.0, 1.0);

      //ambient
      vec3 ambient = surfaceColor * 0.2;
      linearColor += ambient;

       //diffuse
      float diffuseCoefficient = max(0.1, dot(pos, surfaceToLight));
      vec3 diffuse = diffuseCoefficient * surfaceColor * 1.0;
      linearColor += diffuse;

      //specular
      float specularCoefficient = 0.0;
      specularCoefficient = pow(max(0.0, dot(surfaceToCamera, reflect(-surfaceToLight, normPos))), 15.0);

      vec3 specular = specularCoefficient * specularColor * 1.0;
      linearColor += specular;

      lightPos = vec3(-lightPos.x, -lightPos.y, 10.0);
      surfaceToLight = normalize(lightPos - pos);
      surfaceToCamera = normalize(cameraPosition - pos);

      //specular
      specularCoefficient = 0.0;
      specularCoefficient = pow(max(0.0, dot(surfaceToCamera, reflect(-surfaceToLight, normPos))), 5.0);

      specular = specularCoefficient * surfaceColor * 0.5;
      linearColor += specular;

      // out
      fragColor = vec4(linearColor, 0.0);
   } else {
     fragColor = vec4(0.0, 0.0, 0.0, 0.0);
   }
}
