#define WINGNUM 4.
#define CAVATURE 20.

void drawMisc(out vec4 fragColor, in vec2 uv ,vec2 center,float freq){

    vec2 p= uv-center;
    float radius=length(p);
    float angle=atan(p.y,p.x);

    float wings=sin(WINGNUM*angle+CAVATURE*radius*sin(iTime)+CAVATURE*cos(iTime));


    float wingMdf=cos(5.*radius);


    radius=0.3/radius*clamp(wings,.3,1.)*wingMdf;


	fragColor = vec4(radius*sin(iTime),radius*freq*cos(iTime),.2*radius,radius);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float freq=smoothstep(0.1,1.,texture(iChannel0,vec2(0.5,0.5)).y);

    vec2 uv = fragCoord.xy / iResolution.y;
    fragColor=vec4(0);

    drawMisc(fragColor,uv,vec2(0.8,0.5),freq);
}
