void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 center = iResolution.xy / 2.;
    #define radius 200.0
    float r = radius;

    center.x += sin(iTime) * radius; // moves the circle left and right
    r *= (cos(iTime) + 2.0) / 3.; // zooms the circle in and out

    vec4 day =   vec4(1., .85, .28, 1.);
    vec4 night = vec4(.22, 0., .72, 1.);

    float n; // n is the circle

    // this makes the circle boundary
    if (distance(fragCoord, center) > r) {
        n = 0.;
    } else {
        n = 1.;
    }

    float lerpVal = (sin(iTime) + 1.) / 2.;

	fragColor = n * mix(day, night, lerpVal); // vec4(n,n,n,1.);
}
