float cutoff(float a,float b) {    
	return (1.0 + sin(iChannelTime[0]) ) / 2.0 * b + a;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = vec2(fragCoord.x,iResolution.y - fragCoord.y) / iResolution.xy;
	fragColor = texture(iChannel0,uv);

    float darkness = 1.-length(fragColor.rgb);

    vec4 color = vec4(0.0,1.0,1.0,1.0); //cyan

    float o = darkness/cutoff(.88,2.0); //o < 1 == darkness < .8 ; o > 1 == darkness > .8
	float i = darkness/cutoff(.99999999,2.0);

    fragColor += color * o*o; //adding cyan, weighted by a cutoff darkness
   	fragColor.r += i*i; //adding red shifts the cyan to white, weighted by a cutoff darkness
}
