{
    vec4 sum = vec4(0.0);

    float radius = 2.;
    float blur = radius / iResolution.x;

    float dirx = 1.;
    float diry = 1.;


	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y = 1. - uv.y;

    float off = cos(iTime * 3.) / 100.;


    vec4 pixel;

    pixel = texture(iChannel0, uv);


        	uv = fragCoord.xy / iResolution.xy;
   	 	uv.y = 1. - uv.y;

        if(mod(floor(uv.y * iResolution.y / 3.0), 2.0) == 0.0)
        {
            pixel = vec4(0.0,0.0,0.0,1.0);
        }
        else   
        {
            pixel = texture(iChannel0, uv);
        }

    if(uv.y >0.3)
    {
            pixel = texture(iChannel0, uv);


    }

    if(uv.y > 0.7 - off)
    {

        uv.x -= cos(uv.y*100. + iTime*3.) / 100.;
        uv.y -= ((0.3 - uv.y)/0.3) * 0.05*(1.0+cos(iTime*3.0+50.0*uv.y));

    	//uv.y += sin(uv.x*4.*3.14 + iTime*4.) / 200. + uv.y;

            sum += texture(iChannel0,vec2(uv.x - 4. * blur * diry, uv.y - 4. * blur * dirx)) * 0.0162162162;
