    sum += texture(iChannel0,vec2(uv.x,-uv.y)) * 0.2270270270;



    sum += texture(iChannel0,vec2(uv.x - 1. * blur * diry, -uv.y - 1. * blur * dirx)) * 0.1945945946;
    sum += texture(iChannel0,vec2(uv.x - 2. * blur * diry, -uv.y - 2. * blur * dirx)) * 0.1216216216;
    sum += texture(iChannel0,vec2(uv.x - 3. * blur * diry, -uv.y - 3. * blur * dirx)) * 0.0540540541;
    sum += texture(iChannel0,vec2(uv.x - 4. * blur * diry, -uv.y - 4. * blur * dirx)) * 0.0162162162;




    pixel = vec4(sum.rgb,1.0);

     //   pixel = texture(iChannel0, uv);

    }


