float barrelPower = 0.3;
vec2 center = vec2(0.5);
float radius = 0.3;

vec2 distort(vec2 p)
{
    vec2 d = p - center;
    float theta  = atan(d.y, d.x);
    float rad = length(d);
    rad = pow(rad, 1. + barrelPower);
    d.x = rad * cos(theta);
    d.y = rad * sin(theta);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 p;

    center = vec2(0.5) + vec2(sin(iTime), cos(iTime)) * 0.3;
    barrelPower = 0.3 * sin(iTime * 0.3);

    p = distort(uv);

	fragColor = texture(iChannel0, p);
}
