uniform sampler2D tex;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float aberr = 0.95;
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec2 uv_off = uv - vec2(0.5, 0.5);
    vec2 uv_r = uv_off * aberr, uv_g = uv_off, uv_b = uv_off / aberr;
    fragColor = vec4(texture(tex, uv_r).r, texture(tex, uv_g).g, texture(tex, uv_b).b, 1.0);
}
