float fbm(vec2 uv, float theta) {

    float r = 0.0;

    mat2 rot = mat2(cos(theta), sin(theta),
                    -sin(theta), cos(theta)); 
    rot *= 2.0;

    r += 0.5 *  texture(iChannel0, uv).r; uv *= rot;
    r += 0.25 * texture(iChannel0, uv).r; uv *= rot;
    r += 0.125 * texture(iChannel0, uv).r; uv *= rot;
    r += 0.0625 * texture(iChannel0, uv).r; uv *= rot;


    return r;
}

float line(vec2 uv)
{
   	return 1.0 *  abs(uv.y - 0.5);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float time = iTime * 0.001;
    vec2 uv = fragCoord.xy / iResolution.y;
	vec2 nuv = fragCoord.xy / iResolution.y;


    uv *= 3.0;
    uv -= 1.0;
    uv.x -= 5.0;

    vec2 delta = vec2(0.0, 0.2);

    float nn = fbm(vec2(uv.x, 0.6), 90.0) ;
    float yn = fbm(vec2(uv.x* 2.0, 1.0), 90.0)* 30.0;

    uv.y = floor(uv.y * yn) / yn;


    uv.x = floor(uv.x * nn) / nn;
    uv.y += sin(uv.x) + sin(uv.x * 2.0+ 19.0)* 0.5; 


	uv.y += fbm(vec2(uv.x, 1.0), 90.0) * 0.1;


    vec3 col;
    col.r = line(uv - delta);
    col.g = line(uv);
    col.b = line(uv + delta);

    col = pow(col, vec3(4.0));

    fragColor = vec4(col, 1.0);

}
