#define M_PI 3.14159265359

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 center = iResolution.xy / 2.0;
	float Dist = distance(center, fragCoord);
    float Angle = atan((center.y - fragCoord.y), (center.x - fragCoord.x));
    float angle = -20.0 * (iTime - (5.0 * cos((M_PI * iTime) / 5.0))/ M_PI);
    float velocity = 20.0 * (1.0 + sin((M_PI * iTime) / 5.0));

    if(mod(Dist, 20.0) < 10.0 && Dist < 120.0)
        fragColor = clamp((mod(Angle + angle, M_PI) / 4.0) - (1.0 - velocity / 20.0), 0.0, 1.0) * (20.0 / velocity) * vec4(fragCoord / iResolution.xy, 0.5+0.5*sin(iTime), 1.0);
    else
        fragColor = vec4(0, 0, 0, 0);
}
