void mainImage (out vec4 fragColor, in vec2 fragCoord)
{
	float time = iTime;

	vec2 p = fragCoord.xy/iResolution.xy;

    float b1 =  2.0 * sin(p.x+time);
    float b2 =  2.0 * cos(p.x*p.y+ time);
    b1 = clamp(b1,0.85,1.25);
    b2 = clamp(b2,0.85,1.25);

    float mag = 1.925;
    mag += sin(time*0.25)*0.35;
	p*=mag;

    p.y *= 0.015;
    p.x += 2.0;
    float p2 = sin (p.x*(20.0*cos (p.y*12.0)) + time);
	p2 *= cos (p.x*(15.0*sin (p.y*14.0)) + time);

    vec3 col1 = vec3(0.7,0.35,0.7);
    vec3 col2 = vec3(0.5,0.72,0.25);

    col1.r *= b1;
    col2.g *= b2;

    vec3 col = vec3(0.0);

    if (p2<0.0)
        col = col2 * abs(p2*b1);
    else
        col = col1 * p2*b2;



    fragColor = vec4(col.xyz,1.0);    
}
