void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = abs(fragCoord - 0.5 * iResolution.xy) / iResolution.x;

    float speed = 24.0;
    float frequency = 128.0;

    float dist = uv.x + uv.y;

    float color = 0.5 + 0.5*sin(frequency*dist + speed*iTime);

	fragColor = vec4(vec3(color) ,1.0);
}
