/**/  // --- golfed Shane variant : 131

void mainImage(out vec4 f, vec2 v) {
    vec2 p = v/32.;
    p.y *= sign(cos(length(ceil(p))*83.));
    f   += min(p.x = min(length(p = fract(p)), length(--p)) - .3, .4 - p.x) * 9.; }
/**/




