void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 xy = fragCoord.xy/iResolution.xy;
	vec3 color = vec3(0.0);

	vec2 pos = vec2(0.5)-xy;

	float r = length(pos)*2.0;
	float a = atan(pos.y, pos.x);

	float f = cos(a*3.0);
	f = abs(cos(a*3.0));
	f = abs(cos(a*2.5))*0.5+0.3;
	f = abs(cos(a*8.0))*sin(a*6.0)*0.4+0.2;

	color = vec3(
		1.0 - (sin(iTime)-smoothstep(f, f+0.25, r)), // R
		1.0 - (cos(iTime)-smoothstep(f, f+0.1, r)),  // G
		1.0 - (sin(iTime)-smoothstep(f, f+0.5, r))   // B
	);

	fragColor = vec4(color, 1.0);
}
