   	float r = sqrt(pow(uv.x - 0.5, 2.0) + pow(uv.y, 2.0));
    float phi = atan(uv.y, uv.x - 0.5);

    float rn = abs(uv.x - 0.5); //2.0 * pow(r, 10.0);
    phi = (uv.y) * PI;

    uv.x = rn * cos(phi) + 0.25;
    uv.y = rn * sin(phi);

    vec4 fft = texture(iChannel0, vec2(uv.x, 0));
