void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float aspect = iResolution.x / iResolution.y;
	vec2 uv = fragCoord.xy / iResolution.xy;
	vec2 position = 0.5 - uv;
    vec2 uva = vec2(position.x, position.y / aspect);
    float v = uva.x * 10.0 * sin(uva.y * 10.0) - 10.0 * uva.y* sin(uva.x * 10.0);
    float value = 0.08 + 1.0 / (80.0 * sin(iTime * 2.5 + v));    
    vec2 uvd = mod(uva, value) - value / 2.0;
    float c = .01 / sqrt(dot(uvd, uvd));
	float summ = smoothstep(0.4, 0.43, c);
	vec3 color = vec3(summ, summ, summ);    
	fragColor = vec4(color,1.0);
}
