void mainImage( out vec4 c, vec2 p ) {
    c -= c;
    p = ( p / iResolution.xy - vec2( .5, .9 ) ) * vec2( 1e3, 2e4 );

    for ( float i = .1; i < 1. ; i += .1 )
    	//c += 9e1 / abs( i * p.x * p.x + p.y );
        c += .9 / abs( i * p.x * p.x + abs( mod( iDate.w / 9., 8. ) - 16. ) * p.x + p.y );
}
