void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color_1 = vec3(.3,0.5,0.2);
    vec3 color_2 = vec3(1.0,1.0,1.0);
    vec3 color_3 = vec3(1.0,0.0,0.0);
    float offset_1 = smoothstep(0.1,0.11, texture(iChannel0,vec2(0.5,0.5)).x);
	float offset_2 = texture(iChannel0,vec2(0.0001,0.0001)).y * iTime;
    float shaping_1 = smoothstep(0.1,0.45,mod(offset_1,0.5 + uv.y)) 
        - smoothstep(0.55,0.9,mod(offset_2,0.5 + uv.y));
    float shaping_2 = smoothstep(0.4,0.4,uv.x) - smoothstep(0.6,0.6,uv.x);
	 shaping_2 = sin(uv.x - offset_2) - sin(uv.x + 2.0 * offset_2);
    fragColor =  vec4(sin(shaping_1 + uv.x) * color_1 + sin( uv.x + shaping_1) * color_3 + (1.0 - 0.4 * shaping_1 ) * shaping_2 * color_2,1.0);
}
