// Python and OpenGL for Scientific Visualization
// 2017 Nicolas P. Rougier
// See also http://jcgt.org/published/0003/04/01/

const float M_PI = 3.14159265358979323846264338327950288;

float club(vec2 P, float size)
{
   const float t1 = -M_PI/2.0;
   const vec2 c1 = 0.225*vec2(cos(t1),sin(t1));
   const float t2 = t1+2.0*M_PI/3.0;
   const vec2 c2 = 0.225*vec2(cos(t2),sin(t2));
   const float t3 = t2+2.0*M_PI/3.0;
   const vec2 c3 = 0.225*vec2(cos(t3),sin(t3));
   float r1 = length( P - c1*size) - size/4.25;
   float r2 = length( P - c2*size) - size/4.25;
   float r3 = length( P - c3*size) - size/4.25;
   float r4 = min(min(r1,r2),r3);
   const vec2 c4 = vec2(+0.65, 0.125);
   const vec2 c5 = vec2(-0.65, 0.125);
   float r5 = length(P-c4*size) - size/1.6;
   float r6 = length(P-c5*size) - size/1.6;
   float r7 = P.y - 0.5*size;
   float r8 = 0.2*size - P.y;
   float r9 = max(-min(r5,r6), max(r7,r8));


    return min(r4,r9);
}

// Blue or red color according to distance d
vec4 color(float d)
{
    vec3 white = vec3(1.0, 1.0, 1.0);
    vec3 blue  = vec3(0.1, 0.4, 0.7);
    vec3 color = white - sign(d)*blue;
    color *= (1.0 - exp(-4.0*abs(d))) * (0.8 + 0.2*cos(140.0*d));
    color = mix(color, white, 1.0-smoothstep(0.0,0.02,abs(d)) );
    return vec4(color, 1.0);
}


// Move circles using mouse
void mainImage(out vec4 fragColor, in vec2 fragCoord )
{

    vec2 p = 2.0*(gl_FragCoord.xy/max(iResolution.x, iResolution.y))-1.0;
    p = p*vec2(1.0, -1.0) - vec2(0.0,0.4);
    fragColor = color(club(p, 1.0));
}
