// FabriceNeyret2 version 
void mainImage( out vec4 C, vec2 g )
{
	g = cos(g / iResolution.y * 12.);
    float r = length(atan(g.x, -g.y)/6.283 + length(g) + iTime * .2);
    int i = int(3.*fract(r)); 
	C = vec4(.25);   
    C[(i+1)%3] += r = fract(3.*r);
    C[i]       += 1.-r;
    C *= sqrt(C*.5);
}/**/

