void mainImage( out vec4 f, vec2 g )
{
    vec2 s=iResolution.xy,c;

    for(float j=1.,i=j;i++<9.;)
        if(cos((j*=dot(step(c=(.5+cos(vec2(j*17.,j*71.+1.6))*.3)*s,g),vec2(2,1)))*6.-2.+iTime)>=i/50.-.8)
            s=c+(s-c-c)*step(c,g), g=abs(g-c);

    g=abs(g+g-s)-s+1.5;
    f+=(max(g.x,g.y)-f)/s.x;
}
