void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = fragCoord.xy / iResolution.xy;

    float margin = 0.125;
    float radius = 0.125;

    vec2 point = abs(uv - 0.5);
    vec2 corner = vec2(0.5 - margin - radius);
	vec2 offset = max(point - corner, 0.0);

    float distance = length(offset) - radius;

	fragColor = vec4(smoothstep(0.0, margin, distance));
}
