// all hard work is done by FabriceNeyret2, 2017-Dec-23

void mainImage( out vec4 O, vec2 U )
{
    O -= O;
    vec2 R = iResolution.xy, b; 
    U -= .5*R;
    float COUNT = 100., 
          phi = 6.283,
           a0 = iTime * .01, a, r, 
           da =  3. * phi / COUNT;

    for ( float n=0.; n < 3.; n++ )
        a = a0 + da * round ( (n*phi + mod( atan(U.y,U.x) - a0 -da/2. , phi ) +da/2. ) / da ),
        b =  min(R.x, R.y)/4. * ( .65 - .35 * sin(a * fract(a0) * 15.)) * vec2(cos(a),sin(a)),
        O += smoothstep(2.,0., length( U-b - b * clamp(dot(b, U-b) / dot(b, b), 0., 1.)) );
}
