precision highp float;

float hw_bitdepth = 256.0;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	float lMean = 0.5;
    float contrast = 1.;
    vec2 uv = fragCoord.xy / iResolution.xy;
   	vec3 L = vec3(lMean + lMean*(uv.x*2.0-1.0)*(1.-uv.y)*contrast);   
	//vec3 LSBs = mod(vec3(L*hw_bitdepth),1.0);

    //LSBs = floor(LSBs*4.0)/4.0;

    float spatial_dither = step(0.5,texture(iChannel0, uv*4.0 + mod(vec2(iFrame*19, iFrame*13),1.0)).x);

    vec3 bit_h = mod(floor(L.xyz*hw_bitdepth*2.0),2.0); //the 9th bit
    vec3 bit_l = mod(floor(L.xyz*hw_bitdepth*4.0),2.0); //the 10th bit

    L = floor(L*hw_bitdepth)/hw_bitdepth; //just in case :)


    float flicker = mod(float(iFrame),2.0); //0. every other frame
    vec3 full_bit = flicker * bit_h * bit_l;
    vec3 half_bit = ceil((mod(flicker + 1.0,2.0) * bit_h) + (flicker * mod(bit_h + bit_l, 2.0) ) / 2.0);


    if(fragCoord.x > iMouse.x){
        L += (full_bit + spatial_dither * half_bit)/256.0;
    }

    fragColor.xyz = L;
}
