// location of each shape
vec2 HouseLocation = vec2(0.5, 0.25);
vec2 Window1Location = vec2(0.35,0.15);
vec2 Window2Location = vec2(0.65,0.15);
vec2 ChimneyLocation = vec2(0.35,0.31);
vec2 Chimney2Location = vec2(0.65,0.31);
vec2 DoorLocation = vec2(0.47,0.07);
vec2 Door2Location = vec2(0.52,0.07);
vec2 SunLocation = vec2(1.05,0.7);
vec2 TreeTrunkLocation = vec2(0.1,0.07);
vec2 TreeTopLocation = vec2(0.1,0.15);

// Color
vec3 HouseColor = vec3(0.545098,0.0,0.0);
vec3 BackgroundColor = vec3(0.0,0.74902,1.0);
vec3 WindowColor = vec3(0.941176,0.972549,1.0);
vec3 ChimneyColor = vec3(0.0,0.0,0.0);
vec3 DoorColor = vec3(0.55,0.25,0.25);
vec3 SunColor = vec3(1.0,1.0,0.0);
vec3 TreeTrunkColor = vec3(0.75,0.25,0.0);
vec3 TreeTopColor = vec3(0.0,1.0,0.0);

// toggle layer on and off
float HouseOn = 1.0;
float Window1On = 1.0;
float ChimneyOn = 1.0;
float DoorOn = 1.0;
float SunOn = 1.0;
float TreeTrunkOn = 1.0;
float TreeTopOn = 1.0;

// Shape Dimensions
float HouseHeight = 0.1;
float HouseWidth = 0.2;
float Window1Height = 0.04;
float Window1Width = 0.03;
float ChimneyHeight = 0.07;
float ChimneyWidth = 0.03;
float DoorHeight = 0.03;
float DoorWidth = 0.02;
float SunRadius = 0.4;
float TreeTrunkHeight = 0.05;
float TreeTrunkWidth = 0.025;
float TreeTopRadius = 0.05;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    uv.y *= iResolution.y / iResolution.x;
    HouseLocation.y *= iResolution.y / iResolution.x;

    vec4 Background = vec4(BackgroundColor, 1.0);
    vec4 Layer1;
    vec4 Layer2;
   	vec4 Layer3;
    vec4 Layer4;
    vec4 Layer5;
    vec4 Layer6;
    vec4 Layer7;

    float HouseLengthX = (1.0 - step(HouseWidth, length(HouseLocation.x - uv.x)));
    float HouseLengthY = (1.0 - step(HouseHeight, length(HouseLocation.y - uv.y)));

    Layer1 = vec4(HouseColor, (HouseLengthX * HouseLengthY) * HouseOn);

    float Window1LengthX = (1.0 - step(Window1Width, length(Window1Location.x - uv.x)));
    float Window1LengthY = (1.0 - step(Window1Height, length(Window1Location.y - uv.y)));


    Window1LengthX += (1.0 - step(Window1Width, length(Window2Location.x - uv.x)));
    Window1LengthY += (1.0 - step(Window1Height, length(Window2Location.y - uv.y)));

    Layer2 = vec4(WindowColor, (Window1LengthX * Window1LengthY) * Window1On);

    float ChimneyLengthX = (1.0 - step(ChimneyWidth, length(ChimneyLocation.x - uv.x)));
    float ChimneyLengthY = (1.0 - step(ChimneyHeight, length(ChimneyLocation.y - uv.y)));

    ChimneyLengthX += (1.0 - step(ChimneyWidth, length(Chimney2Location.x - uv.x)));
    ChimneyLengthY += (1.0 - step(ChimneyHeight, length(Chimney2Location.y - uv.y)));

    Layer3 = vec4(ChimneyColor, (ChimneyLengthX * ChimneyLengthY) * ChimneyOn);

    float DoorLengthX = (1.0 - step(DoorWidth, length(DoorLocation.x - uv.x)));
    float DoorLengthY = (1.0 - step(DoorHeight, length(DoorLocation.y - uv.y)));

    DoorLengthX += (1.0 - step(DoorWidth, length(Door2Location.x - uv.x)));
    DoorLengthY += (1.0 - step(DoorHeight, length(Door2Location.y - uv.y)));

    Layer4 = vec4(DoorColor, (DoorLengthX * DoorLengthY) * DoorOn);

    Layer5 = vec4(SunColor, 1.0 - step(SunRadius, length(SunLocation.xy - uv.xy)));

    float TreeTrunkLengthX = (1.0 - step(TreeTrunkWidth, length(TreeTrunkLocation.x - uv.x)));
    float TreeTrunkLengthY = (1.0 - step(TreeTrunkHeight, length(TreeTrunkLocation.y - uv.y)));

    Layer6 = vec4(TreeTrunkColor, (TreeTrunkLengthX * TreeTrunkLengthY) * TreeTrunkOn);

    Layer7 = vec4(TreeTopColor, 1.0 - step(TreeTopRadius, length(TreeTopLocation.xy - uv.xy)));

    vec4 Composite = mix(Background, Layer1, Layer1.a);
    Composite = mix(Composite, Layer2, Layer2.a);
    Composite = mix(Composite, Layer3, Layer3.a);
    Composite = mix(Composite, Layer4, Layer4.a);
    Composite = mix(Composite, Layer5, Layer5.a);
    Composite = mix(Composite, Layer6, Layer6.a);
    Composite = mix(Composite, Layer7, Layer7.a);
    fragColor = Composite;
}
