float tCurve()
{
    float t = iTime*.3 * 4.;
	t = floor(t) +  smoothstep(0., 1., fract(t));
	t = mod(t/4., 3.5);

    return t + 2. * (1.75-max(1.75,t));
}

void mainImage( out vec4 O, vec2 p )
{
	p = ( p+p - iResolution.xy ) / iResolution.y;

	float a = atan(p.x,p.y) * ( -1. + tCurve() );

    p *= mat2(cos(a),sin(a),-sin(a), cos(a));

    p = abs(p) - .5;

	O = vec4( smoothstep( .0, .01, abs(max(p.x, p.y)) ) );
}
