info.description:
Due to our poor trichromatic vision, different color spectrums can give the same color perception.
from bottom to top:
1 peak at green, 2 peaks around green, box around green, cos around green, 2 peaks at +-pi/3 around green, 1 peak + const

renderpass.code:
// following https://www.shadertoy.com/view/XldGDM
// next: https://www.shadertoy.com/view/Mtd3WM
// biblio: https://en.wikipedia.org/wiki/Metamerism_(color)
//         https://en.wikipedia.org/wiki/Trichromacy

// for simplicity, cone response is approximated by (1 + cos(3.14*(f-f0) )/2.
//                                                  with f0 = -2/3, 0, 2/3
// f in [-1 (red) , 1 (violet) ]
// perception = int( colorSpectrum(f) * coneResponse(f) )/2.

// ATTENTION:
// - there might still be some normalization bug ;-)
// - next shader is more about surface color while this one is about volumes

#define RGB(f) ( .5 + .5* cos(3.14*f + vec4(2.1, 0,-2.1, 1)) )
void mainImage( out vec4 O,  vec2 U )
{
    O -= O;
    vec2 R = iResolution.xy;
    U /= R;
    float y = 6.*U.y, a = fract(y), pa=3.14*a;
    if (a < 6./R.y) return;

    // decompose a pure frequency color into R,G,B using kernel (1+cos)/2
    O =   y > 5. ?    a + (1.-a)*RGB(0.)                   // peak + const a
        : y > 4. ?  ( RGB(-1./3.) + RGB(1./3.) ) / 2.      // 2 peaks around green
        : y > 3. ?   .5 + .5* vec4(-1./4.,1./2.,-1./4.,1)  // cos around green
        : y > 2. ?   .5 + .5* sin(pa)/pa*vec4(-.5,1,-.5,1) // box around green
        : y > 1. ? ( RGB(-a/2.) + RGB(a/2.) ) / 2.         // 2 peaks around green
        :            RGB(0.);                              // 1 peak at green

    // O /= max(O.r,max(O.g,O.b)); // chrominance only
}
