#define lim 1200.
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 c = fragCoord/iResolution.xy - .5;
    c.x *= iResolution.x/iResolution.y;
    float m = pow(2., iTime)-1.;
    c /= m;
    c += vec2(-1.530052
              ,0.);
    vec3 col;
    vec2 z = vec2(0.);

    float i= 0.;

    for (i; i < lim; i++)
    {
        z = mat2(z,-z.y,z.x) * z+c; //I definitely didn't steal this from @techepic.


        if (length(z)> 2.) break;
    }
    col = vec3(i/lim*(iTime*0.1+1.));
    fragColor = vec4(col,0.);
}
