void disk(vec2 r, vec2 center, float radius, vec3 color, inout vec3 pixel) {
	if( length(r-center) < radius) {
		pixel = color;
	}
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 r =  2.0*vec2(fragCoord.xy - 0.5*iResolution.xy)/iResolution.y;

    vec3 bgColor = vec3(0.878,0.894,0.8);
    vec3 color1 = vec3(0.411,0.823,0.905);
    vec3 color2 = vec3(0.952,0.525,0.188);

    vec3 pixel = bgColor;

    vec2 center;

    // circle at the center
    center = vec2(0.0, 0.0);
    disk(r, center, 0.3, color1, pixel);

    // circle shifted
    center = vec2(0.9, -0.4);
    disk(r, center, 0.6, color2, pixel);

    // Output to screen
    fragColor = vec4(pixel,1.0);
}
