void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;

   	float xAmount = 0.0;
    float yAmount = 0.0;

	vec2 mousePos = iMouse.xy / iResolution.xy;

    xAmount = 0.25 - mousePos.x * 0.5;
    yAmount = 0.25 - mousePos.y * 0.5;


    vec3 col;
    col.r = texture( iChannel0, vec2(uv.x + xAmount, uv.y + yAmount) ).r;
    col.g = texture( iChannel0, vec2(uv.x, uv.y) ).g;
    col.b = texture( iChannel0, vec2(uv.x - xAmount, uv.y - yAmount) ).b;

    fragColor = vec4(col,1.0);
}
