void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
	fragColor = texture(iChannel0, uv);
}

vec4 getPixel(vec2 pos, float x, float y, vec2 scale)
{
    return texture(iChannel0, ((pos + vec2(x,y)) * scale));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{    
    vec2 scale = 1.0 / iResolution.xy;

    vec4 hereColor = getPixel(fragCoord, 0.0, 0.0, scale);
    vec4 accum = getPixel(fragCoord, -1.0, -1.0, scale)
        		+ getPixel(fragCoord, 0.0, -1.0, scale)
        		+ getPixel(fragCoord, 1.0, -1.0, scale)
        		+ getPixel(fragCoord, -1.0, 0.0, scale)
        		+ getPixel(fragCoord, 1.0, 0.0, scale)
        		+ getPixel(fragCoord, -1.0, 1.0, scale)
        		+ getPixel(fragCoord, 0.0, 1.0, scale)
        		+ getPixel(fragCoord, 1.0, 1.0, scale);

    bool alive = hereColor.r == 1.0;


    if (alive)
    {
        alive = accum.r >= 2.0 && accum.r <= 3.0;
    }
    else
    {
        alive = accum.r == 3.0;
    }

    if (iMouse.z > 0.0)
    {
        float len = length(iMouse.xy - fragCoord);
        if (len < 10.0)
        {
            alive = true;
        }
    }

    if (iFrame == 0)
    {
        alive = (cos(fragCoord.x * 2.0) + sin(fragCoord.y)) > 0.0;
    }

    if (alive)
    {
        fragColor = vec4(1,1,1,1);
    }
    else   
    {
        fragColor = vec4(0,0,0,0);
    }
}
